    // Geração dos links das corretoras, incluindo HTX
    function generateExchangeLink(exchange, pair, market) {
      const exchangeConfig = {
        'gate.io': {
          spot: {
            url: (pair) => `https://www.gate.io/pt-br/trade/${pair}`,
            format: (p) => p.replace('/', '_').toUpperCase()
          }
        },
        'mexc': {
          spot: {
            url: (pair) => `https://www.mexc.com/pt-PT/exchange/${pair.replace('/', '_')}`,
            format: (p) => p.replace('/', '_').toUpperCase()
          },
          future: {
            url: (pair) => `https://futures.mexc.com/pt-PT/exchange/${pair.replace('/', '_')}?type=linear_swap`,
            format: (p) => p.replace('/', '_').toUpperCase()
          }
        },
        'htx': {
          spot: {
            url: (pair) => `https://www.htx.com/trade/${pair}`,
            format: (p) => p.toLowerCase().replace('/', '_')
          },
          future: {
            url: (pair) => `https://www.htx.com/trade/${pair}?type=future`,
            format: (p) => p.toLowerCase().replace('/', '_')
          }
        }
      };
      const normalizedExchange = exchange.toLowerCase().replace('gateio', 'gate.io').trim();
      if (!exchangeConfig[normalizedExchange] || !exchangeConfig[normalizedExchange][market]) {
        return '#';
      }
      const formattedPair = exchangeConfig[normalizedExchange][market].format(pair);
      try {
        return exchangeConfig[normalizedExchange][market].url(formattedPair);
      } catch (error) {
        return '#';
      }
    }
  
